import React, { useState } from "react";
import PropTypes from "prop-types";

const TableHeader = ({ onSort, selectedSort, columns }) => {
    const handleSort = (item) => {
        if (selectedSort.path === item) {
            onSort({
                ...selectedSort,
                order: selectedSort.order === "asc" ? "desc" : "asc"
            });
        } else {
            onSort({ path: item, order: "asc" });
        }
    };

    // const setArrow = (col) => {
    //     Object.keys(columns).map((column) => {
    //         if (
    //             columns[column].name === col &&
    //             columns[column].name !== "Kачества" &&
    //             columns[column].name !== undefined
    //         ) {
    //             console.log(columns[column].name);
    //             return <i className="bi bi-caret-down-fill"></i>;
    //         }
    //     });
    // };

    const addArrow = (column) => {
        Object.keys(columns).map((column) => {
            columns[column] === column
                ? (columns[column].arrowDirection: "down")
                : "up";
            console.log("arrowDirection: ", columns[column].arrowDirection);
        });
    };

    return (
        <thead>
            <tr>
                {Object.keys(columns).map((column) => (
                    <th
                        key={column}
                        onClick={
                            columns[column].path
                                ? () => handleSort(columns[column].path)
                                : undefined
                        }
                        onClick={() => addArrow(columns[column])}
                        {...{ role: columns[column].path && "button" }}
                        scope="col"
                        className="unselectable"
                    >
                        {columns[column].name}
                    </th>
                ))}
            </tr>
        </thead>
    );
};

TableHeader.propTypes = {
    onSort: PropTypes.func.isRequired,
    selectedSort: PropTypes.object.isRequired,
    columns: PropTypes.object.isRequired
};

export default TableHeader;
