import React, { useState } from "react";
import api from "../api";

const Films = () => {
  const [films, setFilms] = useState(api.films.fetchAll());

  const handleDelete = (filmId) => {};

  const renderPhrase = (number) => {};

  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Фильм</th>
          <th scope="col">Качества</th>
          <th scope="col">Жанр</th>
          <th scope="col">просмотрел</th>
          <th scope="col">оценка КП</th>
        </tr>
      </thead>
      <tbody>
        {films.map((film) => (
          <tr key={film._id}>
            <td>{film.name}</td>
            <td>
              {film.qualities.map((item) => (
                <span key={item._id}>{item}</span>
              ))}
            </td>
            <td>{film.genereOfFilm.name}</td>
            <td>{film.viewed}</td>
            <td>{film.genereOfFilm}</td>
            <td>{film.rate}</td>
            <td>
              <button className="bg-danger">delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Films;
