import React, { useEffect, useState } from "react";
// import PropTypes from "prop-types";
import api from "../../api";
import { useParams } from "react-router-dom";

const FilmPage = () => {
    const { film } = useParams();
    // const currentFilmId = api.getById(film);
    // console.log(currentFilmId);

    const [currentFilmId, setCurrentFilmId] = useState();

    useEffect(() => {
        api.films.getById(film).then((data) => setCurrentFilmId(data));
    }, []);
    console.log(currentFilmId);

    return (
        <>{currentFilmId ? <h1>{currentFilmId.name}</h1> : <p>loading...</p>}</>
    );
};

export default FilmPage;
