import React from "react";
// import Film from "./film";
import PropTypes from "prop-types";
// import TableHeader from "./tableHeader";
// import TableBody from "./tableBody";
import BookMark from "./bookmark";
import Qualitie from "./qualitie";
import Table from "./table";

const FilmsTable = ({
    films,
    onToggleBookMark,
    onDelete,
    filmCrop,
    onSort,
    selectedSort
}) => {
    const columns = {
        name: { path: "name", name: "Имя" },
        qualities: {
            name: "Kачества",
            component: (film) => <Qualitie film={film} />
        },
        films: { path: "genereOfFilm.name", name: "Жанр" },
        viewed: { path: "viewed", name: "к-во просмотров" },
        rate: { path: "rate", name: "оценка" },
        bookmark: {
            path: "bookmark",
            name: "избранное",
            component: (film) => (
                <BookMark
                    status={film.bookmark}
                    onToggleBookMark={onToggleBookMark}
                    filmId={film._id}
                />
            )
        },
        delete: {
            component: (film) => (
                <button
                    className="text-light btn bg-danger"
                    onClick={() => onDelete(film._id)}
                >
                    delete
                </button>
            )
        }
    };

    var isPalindrome = function (x) {
        var str = `${x}`;
        var testStrMass = Array.from(str);
        console.log("testStrMass.reverse: ", testStrMass);
        testStrMass.reverse();
        console.log("testStrMass.reverse: ", testStrMass);

        var reversedStr = testStrMass.join("");
        console.log(reversedStr);

        return x === reversedStr ? true : false;
    };
    console.log(isPalindrome(121));
    console.log(isPalindrome(-121));
    console.log(isPalindrome(10));

    return (
        <Table
            onSort={onSort}
            selectedSort={selectedSort}
            columns={columns}
            data={films}
        />
    );
};

FilmsTable.propTypes = {
    films: PropTypes.array.isRequired,
    onDelete: PropTypes.func.isRequired,
    onToggleBookMark: PropTypes.func.isRequired,
    filmCrop: PropTypes.array.isRequired,
    onSort: PropTypes.func.isRequired,
    selectedSort: PropTypes.object.isRequired
};

export default FilmsTable;
