import React from "react";
import Film from "./film";
import PropTypes from "prop-types";

const FilmsTable = ({ films, onToggleBookMark, onDelete, filmCrop }) => {
    return (
        <table className="table">
            <thead>
                <tr>
                    <th onClick={() => onSort("name")} scope="col">
                        Фильм
                    </th>
                    <th scope="col">Качества</th>
                    <th onClick={() => onSort("genereOfFilm.name")} scope="col">
                        Жанр
                    </th>
                    <th onClick={() => onSort("viewed")} scope="col">
                        просмотрел
                    </th>
                    <th
                        onClick={() => onSort("rate")}
                        className="text-center"
                        scope="col"
                    >
                        оценка
                    </th>
                    <th onClick={() => onSort()} scope="col">
                        закладки
                    </th>
                </tr>
            </thead>
            <tbody>
                <Film
                    films={films}
                    onDelete={onDelete}
                    onToggleBookMark={onToggleBookMark}
                    filmCrop={filmCrop}
                />
            </tbody>
        </table>
    );
};

FilmsTable.propTypes = {
    films: PropTypes.array.isRequired,
    onDelete: PropTypes.func.isRequired,
    onToggleBookMark: PropTypes.func.isRequired,
    filmCrop: PropTypes.number.isRequired
};

export default FilmsTable;
