import React, { useState } from "react";
import api from "../api";

const Films = () => {
  const [films, setFilms] = useState(api.films.fetchAll());

  const handleDelete = (filmId) => {};

  const renderPhrase = (number) => {};

  return (
    <>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Фильм</th>
            <th scope="col">Качества</th>
            <th scope="col">Жанр</th>
            <th scope="col">просмотрел</th>
            <th scope="col">оценка КП</th>
          </tr>
        </thead>
        <tbody>
          {films.map((film) => (
            <tr key={film._id}>
              <td className="fs-6">{film.name}</td>
              <td>
                {film.qualities.map((item) => (
                  <span className={"badge m-1 bg-" + item.color} key={item._id}>
                    {item.name}
                  </span>
                ))}
              </td>
              <td>{film.genereOfFilm.name}</td>
              <td>{film.viewed}</td>
              <td>{film.rate}</td>
              <td>
                <button className="btn bg-danger">delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Films;
